package basicjavafunction;

public class Loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Initialize condition increment 0r Decrement}
		
		//For Loop
		for(int i=1; i<10; i++)
		{
			System.out.println(i);
		}
		
		
		//While Loop
		int j=0;
		while(j<10)
		{
			System.out.println(j);
			j++;
		}
		
		//Do While loop
		
		int x=0;
		do{
			System.out.println(x);
			x++;
		}while(x<10);
	}
	
}
	
